import './device_variables.css';
import React, { useEffect, useState } from 'react';

const { tago_device } = require('../../classes/device/device.js');
const { location_apis } = require('../../classes/location/location.js')


function Device_variables(){
  const [device_variables, set_device_variables] = useState([
    {
        id: "644011bea99b070009c90be1",
        time: "2023-04-19T16:05:19.000Z",
        value: "0-4242117",
        variable: "esn",
        location: {
            type: "Point",
            coordinates: [
                -46.63644267,
                -23.5955225
            ]
        },
        metadata: {
           raw_data: "esn;0-4242117;model,AT-G1018;hmv,0.1M4;fmv,0.8M4;mode,2;media,GSM/GPRS;rtc,2023-04-19 16:02:00;battery_volts,4.04;imei,869951032048823;iccid,8944500601200071406F;cops,CLARO BR;jamm,+SJDR: NO JAMMING;rf_model,HC-12;rf_channel,001;rmc,$GNRMC,160519.000,A,2335.73135,S,04638.18656,W,000.0,172.0,190423,,,A,V*07;vtg,$GNVTG,172.0,T,,M,000.0,N,000.0,K,A*17;zda,$GNZDA,160519.000,19,04,2023,00,00*4D;psti20,$PSTI,20,1,1,1,0,A,0,1,-39.88,100.00,0.00,0.00*4B;psti60,$PSTI,060,1,V,60.11,-53.88,171.97,,,,,,,,*75;mac0,cc:32:e5:11:0b:80;mac1,ce:32:e5:21:0b:80;mac2,c0:3d:d9:10:79:f0;lbs_mode,LTE",
            model: "AT-G1018",
            hmv: "0.1M4",
            fmv: "0.8M4",
            mode: "2",
            media: "GSM/GPRS",
            rtc: "2023-04-19T16:05:19.000Z",
            battery_volts: "4.04",
            battery: null,
            imei: "869951032048823",
            iccid: "8944500601200071406F",
            cops: "CLARO BR",
            jamm: "NO JAMMING",
            rf_model: "HC-12",
            rf_channel: "001",
            rmc: "$GNRMC,160519.000,A,2335.73135,S,04638.18656,W,000.0,172.0,190423,,,A,V*07",
            vtg: "$GNVTG,172.0,T,,M,000.0,N,000.0,K,A*17",
            spd: "0.0",
            cog: "172.0",
            direction: "S",
            zda: "$GNZDA,160519.000,19,04,2023,00,00*4D",
            psti20: "$PSTI,20,1,1,1,0,A,0,1,-39.88,100.00,0.00,0.00*4B",
            psti60: "$PSTI,060,1,V,60.11,-53.88,171.97,,,,,,,,*75",
            mac0: "cc:32:e5:11:0b:80",
            mac1: "ce:32:e5:21:0b:80",
            mac2: "c0:3d:d9:10:79:f0",
            lbs_mode: "LTE",
            url_pin: {
                "url": "https://www.google.com/maps/search/?api=1&query=-23.59552250,-46.63644267",
                "alias": "Open map at -23.59552250,-46.63644267"
            },
            link: "https://www.google.com/maps/search/?api=1&query=-23.59552250,-46.63644267",
            lat: "-23.59552250",
            lon: "-46.63644267",
            IEC: "V",
            address: "Rua Doutor Tirso Martins - Vila Mariana - S達o Paulo - S達o Paulo - 04037-000",
            origin: "GPS",
            dr_cal: 1,
            gyro_cal: 1
        },
        group: "0eb09c900070b99aeb110446",
        device: "6419c2dea997d700090c60ae"
    }
]);

  const device_methods = new tago_device();
  const location_funcs = new location_apis();




     window.TagoIO.onStart( async(widget) => {
        window.widget = widget;

        let device_id = widget.display.variables[0].origin.id;
        let request = await device_methods.get_device_variables(device_id); 
        set_device_variables(request.filter(obj => obj.variable === "esn")); 
    })
    window.TagoIO.ready(); 
  
  

    
    
   

   function add_0_to_left(value){

      if( String(value).length === 1 ){
        return "0" + value;
      }else{
        return value;
      }

   }



    return(
        <>

        <div className='div-mother'>
        <div className='div-filho'>

      <div className='text-div-mother'>
         <h1>See all variables that the selected device sent!</h1>
         <br/>
        </div>
       
                <div className='data_variables'>

                        <table border="1" className='variable_tables'>
                            <tr>
                                <td>Origin</td>
                                <td>Date and Time</td>
                                <td>Coordinates</td>
                                  
                            </tr>
                            {device_variables.map(data => { 

                                setTimeout(() => {// This delay is necessary because firt i need to create an element with the corresponding tag and after i catch the element through of id 
                                    const element_id = data.id;
                                    const scope = data; 

                                    document.getElementById(data.id).addEventListener('change', async function(){
                                      
                                        if(this.value === "MAC"){ 
                                          let element_to_insert_the_new_value = document.getElementsByClassName(element_id)[0];
                                          let element_to_insert_the_address = document.getElementsByClassName(element_id)[1]; 

                                          let mac_coordinates = await location_funcs.get_coordinates_through_mac_datas(["mac0", "mac1", "mac2"],scope); 
                                          let address = await location_funcs.get_address_through_coordinates(mac_coordinates.lat, mac_coordinates.lng);
                                         
                                          element_to_insert_the_new_value.innerText = `${mac_coordinates.lat},${mac_coordinates.lng}`;
                                          element_to_insert_the_address.innerText = address;
                                        }
                                        else if(this.value === "LBS"){
                                          let element_to_insert_the_new_value = document.getElementsByClassName(element_id)[0];
                                          let element_to_insert_the_address = document.getElementsByClassName(element_id)[1];//Position 1 because i yhave 2 elements with the same tags, the second element will render the address

                                          let lbs_coordinates = await location_funcs.get_coordinates_through_lbs_datas(["lbs0", "lbs1", "lbs2"],scope, scope.metadata.lbs_mode === "LTE" ?"lte" :"gsm" ); 
                                          let address = await location_funcs.get_address_through_coordinates(lbs_coordinates.lat, lbs_coordinates.lng);
                                         
                                          element_to_insert_the_new_value.innerText = `${lbs_coordinates.lat},${lbs_coordinates.lng}`; 
                                          element_to_insert_the_address.innerText = address;
                                        }
                                        else{ 
                                          let element_to_insert_the_coordinates = document.getElementsByClassName(element_id)[0];
                                          let element_to_insert_the_address = document.getElementsByClassName(element_id)[1]; 

                                          let gps_coordinates = { lat:scope.metadata.lat, lng:scope.metadata.lon };
                                          let address = await location_funcs.get_address_through_coordinates(gps_coordinates.lat, gps_coordinates.lng);
                                          
                                          element_to_insert_the_coordinates.innerText = `${gps_coordinates.lat},${gps_coordinates.lng}`;
                                          element_to_insert_the_address.innerText = address;
                                        
                                        }

                                    })

                                },10);
                            
                              return( 
                                <tr>
                                    <td className='data_variable'>{data.metadata.origin}</td>

                                     <td>{`${ add_0_to_left((data.time.getMonth()) + 1)  }/${ add_0_to_left(data.time.getDate()) }/${ add_0_to_left(data.time.getFullYear()) } ${ add_0_to_left(data.time.getHours()) }:${ add_0_to_left(data.time.getMinutes()) }:${ add_0_to_left(data.time.getSeconds()) }`}</td> 

                                    <td className='b'>
                                        <select id={data.id} className ="coordinate-types">
                                            <option>Select the data type that you want to see...</option>
                                            <option value="LBS" >LBS coordinates</option>
                                            <option value="MAC" >MAC coordinates</option>
                                            <option value="GPS" >GPS coordinates</option>
                                        </select>

                                     
                                     </td>

                                     <td className={data.id}></td>
                                     <td className={data.id}></td>

                                </tr>
                              )

                               
  


                            })}

                        </table>
                        
                            
                    
                </div>




                </div> 


           

            </div>         
         
        </>
    )
}



export { Device_variables };
